% funkcja sklejana
function y = FunkcjaSklejana(x)
    X = [ 20, 50, 100, 150, 200, 250, 280, 300   ];   %% local data + aprox data
    Y = [ 0.46 , 0.64 , 0.78 , 0.68 , 0.44 , 0.23 , 0.18 , 0.18  ];
    if (x>=X(1)) 
        if (x<=X(end))
        for i=1:length(X)-1
            if  ( x<=X( i+1 ))
                xx=x-X(i);
                H=(Y(i+1))-(Y(i)); 
                W=(X(i+1))-(X(i)); 
                y=Y(i)+(xx*H)/W; 
                return
            end % H/z proporcji H/y=W/x
        end    
        end
    end
end

